import timefrom machine import Pinpinout = Pin(4, Pin.OUT)pinin = Pin(14, Pin.IN, Pin.PULL_UP)  #D5zmiana = 0a=0if (pinin.value()==0):  pinout.value(1)else:  pinout.value(0)  def handle_interrupt(pin):    time.sleep(1)    if (pinin.value()==0):        pinout.value(1)    else:        pinout.value(0)    print("inter")    def handle_interrupt2(pin):    time.sleep(1)    pinout.value(0)    print ("inter2")      pinin.irq(trigger=3, handler=handle_interrupt)#pinin.irq(trigger=Pin.IRQ_RISING, handler=handle_interrupt2)